# vim: expandtab shiftwidth=4 ft=perl6

sub palindromic(@list) {
    return True if @list < 2;

    my $start = 0;
    my $end = @list.end;
    my $mid_start = floor(@list / 2 - 1);
    my $mid_end = ceiling(@list / 2);
    @list[$start .. $mid_start] ~~ @list[$mid_end .. $end].reverse.list;
}

my @examples = [
    [[< a b c d E >], False],
    [[< a b c b a >], True],
    [[< a b b E >], False],
    [[< E b b a>], False],
    [[< a b b a >], True],
    [[< a >], True],
    [[< a a >], True],
    [[< E a >], False] ];

for @examples -> ($list, $result) {
    my $is-result = $result ?? "" !! "not ";
    if palindromic($list) != $result {
        die "{$list.perl} was expected {$is-result}to be a palindrome, but isn't";
    }
    else {
        say $list ~ " is {$is-result}a palindrome";
    }
}

# vim: expandtab shiftwidth=4 ft=perl6

# Create a list palindrome
my @l = <A B C B A>;
# Reverse the list (see P05)
# Then compare it
#       ==      This will compare two lists, and return true if they contain the same
#       elements - but it does not care about the order of the elements.
#       eq  This will turn the list into a string, and compare those
if (@l eq @l.reverse) {
    say "{@l}", ' is a palindrome';
}
else {
    say "{@l}", ' is not a palindrome';
}

