# Robert Eaglestone  22 sept 09
#
#   My first Perl6 script - I'm sure this can be done better
#
my @in = <a a a a b c c a a d e e e e>;
my @out = pack_dup( @in );
@out.perl.say;

sub pack_dup(@in) {
    my @out = [ [@in.shift], ];

    for @in -> $elem {
        push @out, [] if $elem ne @out[*-1][0];
        push @out[*-1], $elem;
    }
    return @out;
}

# vim: expandtab shiftwidth=4 ft=perl6

my @l = <a a a a b c c a a d e e e e>;
sub packit (@in) {
    my @out;
    my @last = [shift @in,];
    for @in -> $t {
        if (@last[0] ne $t) {
            push @out, @last;
            @last := [$t,];
        }
        else {
            push @last, $t;
        }
    }
    if (@last.elems) {
        push @out, @last;
    }
    return @out;
}
say ~@l;
say packit(@l).perl;

# vim: expandtab shiftwidth=4 ft=perl6

sub pack-consecutive-dups(@list)
{
    gather while @list.elems
    {
        my $last = @list[0];

        take [
            gather while @list.elems
                   and @list[0] ~~ $last
            {
                take ($last = @list.shift)
            }
        ]
    }.list
}

say pack-consecutive-dups([<a a a a a b b c b d e e>]).perl;

