sub encode(@ls)
{
    my @list = @ls[*];
    gather while @list.elems {
        my $value = @list[0];
        my $count = 0;

        while @list.elems and @list[0] ~~ $value {
            $count++;
            shift @list
        }

        take [$count, $value];
    }
}

