    return gather loop {
        last if !@in.elems;
        my $val = @in[0];
        take [
            gather {
                loop {
                    last if !@in.elems;
                    @in[0] ~~ $val ?? take(shift @in) !! last
                };
            }.elems, $val
        ];
    };
}
