# A simple implementation of Eratosthenes' sieve
sub primes_iterator {
    return sub {
        state %D;
        state $q //= 2;
        while %D{$q}:exists {
            my $p = %D{$q}:delete;

            my $x = $q + $p;
            $x += $p while %D{$x} :exists;
            %D{$x} = $p;
            ++$q;
        }
        %D{$q * $q} = $q;
        return $q++;
    }
}

