# A simple implementation of Eratosthenes' sieve. Modified to avoid
# registering stuff beyond the $upper_bound.
sub primes_iterator {
    return sub {
        state %D;
        state $q //= 2;
        while (%D{$q}:exists) {
            my $p = %D{$q};

            my $x = $q + $p;
            $x += $p while %D{$x}:exists;
            %D{$x} = $p if $x <= $upper_bound;
            ++$q;
        }
        my $q2 = $q * $q;
        %D{$q2} = $q if $q2 <= $upper_bound;
        return $q++;
    }
}

