sub d(Int $n) {
    my $sum = 1;
    my $sqrt-n = sqrt $n;

    for 2..Int($sqrt-n) -> $a {
        my $b = $n div $a;
        $sum += $a + $b if $a * $b == $n;
    }

    $sqrt-n ~~ Int ?? $sum - $sqrt-n !! $sum;
}

