sub find_cycle_len(Int $n) returns Int {
    my %states;

    my $r = 1;
    my $count = 0;

    while ! ( %states{$r}:exists ) {
        # $*ERR.say( "Trace: N = $n ; R = $r" );
        %states{$r} = $count++;
        ($r *= 10) %= $n;
    }

    return $count - %states{$r};
}

