}

sub is-pandigital(Int $n is copy) {
    return unless 123456789 <= $n <= 987654321;
    my $x = 0;
    loop ( ; $n != 0 ; $n div=10) {
        my $d = $n mod 10;
        $x += $d * 10 ** (9 - $d);
    }
    $x == 123456789;
}

