sub next-triplet([\a,\b,\k], \N) {

    # finding minimal l
    1 .. N.sqrt.floor
        ==> grep  -> \l { (a + b * l) %% k } \
        ==> sort  -> \l { abs(l ** 2 - N)  } \
        ==> my @r;

    my \l = @r.shift;

      (a * l + N * b) / abs(k)
    , (a + b * l)     / abs(k)
    , (l ** 2 - N )   / k
}

sub simple-solution(NonSquarable \N) {

    my $a = N.sqrt.floor;
    my $b = 1;
    my $k = $a ** 2 - N;

    $a, $b, $k;
}

sub chakravala(NonSquarable \N) {
    # Start with a solution for a - N b = k

    my ($a, $b, $k) = simple-solution N;

    ($a,$b,$k) = next-triplet [$a,$b,$k], N
        while $k != 1;

    $a, $b, $k;
}


