my constant $limit = 100;

sub sqrt-subtraction($n) {
    my Int $a = $n * 5 ;
    my Int $b = 5;
    while $b < 10 * 10 ** $limit {
        given $a <=> $b {
            when More | Same {
                # replace a with a  b, and add 10 to b.
                $a -=  $b;
                $b += 10;
            }
            when Less {
                # add two zeros to a
                $a *= 100;
                # add a zero to b just before the final digit (which will always be 5).
                $b = ($b - (my $x = $b % 10)) * 10 + $x;
            }
        }
    }
    $b;
}

