        next if $n.sqrt.floor ** 2 == $n;
        my $x = [+] $n.&sqrt-subtraction.comb[^$limit];
        say "$n $x"  if $verbose;
        $x;
    }
    say "Done in {now - INIT now}" if $verbose;
}

