sub power-mod($b is copy, $e is copy, $m is copy
                                       where $b & $e & $m > 0)  {
    my $r = 1;
    while $e != 0 {
        $r = $r * $b mod $m if $e !%% 2;
        $e = floor $e/2;
        $b = $b ** 2 mod $m
    }
    $r;
}

