sub MAIN($input-file = $*SPEC.catdir($*PROGRAM-NAME.IO.dirname, "revcomp.input")) {
    my ($desc, @seq);

    for $input-file.IO.lines {
        dump, $desc = $_, next if /^ \>/;
        @seq.unshift(|.comb.reverse.map({ %trans{$^c} // $^c }));
        LAST dump;
    }

    sub dump {
        LEAVE @seq = Empty;
