our %count_for;

sub MAIN(Str $orders = $*SPEC.catdir($*PROGRAM-NAME.IO.dirname, 'coffee.txt')) {
    # get the data
    my @contents = $orders.IO.lines;

    # count the orders
    for @contents -> $line {
        unless $line ~~ /^ (Office \s+ \d+) $/ {
            my ($drink, $count) = $line.split(/\s+/);
            %count_for{$drink} += $count;
        }
    }

    # print to screen
    for %count_for.kv -> $drink, $total {
        say $drink ~ ": " ~ $total;
    }
}

